mapper.select:
select 
	* 
from 
( 
	select 
		rownum rowx, x.* 
	from 
		(select * from schedule_job j 


					order by ${sidx} ${order}


					order by j.job_id desc 


		)x



			x.bean_name like '%' || #{beanName} || '%'


			and rownum <= ${offset} + ${limit}


)

	where 
		rowx > ${offset}

mapper.update:
update schedule_job
bean_name = #{beanName}, method_name = #{methodName}, params = #{params}, cron_expression = #{cronExpression}, status = #{status}, remark = #{remark},
where job_id = #{jobId}
