mapper.select:
select * from ( select rownum rowx, x.* from (select * from sys_config c
order by ${sidx} ${order}
order by c.id desc
)x
where status = 1
and key like '%' || #{key} || '%'
and rownum <= ${offset} + ${limit}
)
where rowx > ${offset}

mapper.delete:
beagin
delete from sys_config where id in 

	#{id}

;end;
